import{_ as a,c as i,o as s,a as e,b as t}from"./app.23c7550c.js";const f=JSON.parse('{"title":"记换内存引来的一次 bios 灾难","description":"","frontmatter":{},"headers":[],"relativePath":"blogs/whatever/记换内存引来的一次bios灾难.md","lastUpdated":1693335640000}'),o={name:"blogs/whatever/记换内存引来的一次bios灾难.md"},r=e("h1",{id:"记换内存引来的一次-bios-灾难",tabindex:"-1"},[t("记换内存引来的一次 bios 灾难 "),e("a",{class:"header-anchor",href:"#记换内存引来的一次-bios-灾难","aria-hidden":"true"},"#")],-1),n=e("p",null,[t("由于要跑 ROS2 内存太小有些困难，我把老的 d4 3600 8g* 2 内存换成了 16g* 2 3600，结果再启动 windows 启动不了了。诡异的是，此时双系统中的 Arch Linux 仍然能启动。我花了一晚上排查问题，发现是 intel 的 VMD 在我重置 bios 的时候被设成了 enabled。把 intel 的 VMD 关掉之后，一些盘读不出来了，arch linux 的 grub 也掉了（因为之前修复了一下 windows，估计是那个时候把 archlinux 干掉了）但是能够进入 win11。这个时候我用 ez update 更新 bios 结果华硕不支持在 windows 中 update 了。通过 ez flash 更新 bios 之后，bios 提醒我要再次开启 intel VMD，我打开了，但是 win11 启动又没有成功。我一怒之下直接关掉了 VMD，再启动的时候 win11 先进入了排查模式说我重启出了问题，但是可以直接进入系统，然后我点直接进入系统重启了一下就又成功进入 win11 了。所以，启动不成功完完全全是 intel VMD 的问题。这个时候，要解决 arch linux 的 grub 问题了，因为我用的是 btrfs 系统，那个系统命令太长太难记了，"),e("a",{href:"https://arch.icekylin.online/guide/rookie/basic-install.html#_7-%E5%88%86%E5%8C%BA%E5%92%8C%E6%A0%BC%E5%BC%8F%E5%8C%96-%E4%BD%BF%E7%94%A8-btrfs-%E6%96%87%E4%BB%B6%E7%B3%BB%E7%BB%9F",target:"_blank",rel:"noreferrer"},"所以根据 arch-guide 重写了一下 grub"),t("，重启引导成功了，证明之前的猜想是正确的。转眼已是晚上 11 点半，只能写下这篇文章洗洗睡了。")],-1),c=[r,n];function d(l,_,b,h,u,p){return s(),i("div",null,c)}const E=a(o,[["render",d]]);export{f as __pageData,E as default};
